import { Component } from '@angular/core';
import { User } from 'src/app/models/user';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  user: User = new User();

  ngOnInit() {
    this.user.id_user = 1;
    this.user.name = 'Nombre del Usuario';
    this.user.last_name = 'Apellidos del Usuario';
    this.user.email = 'correo@ejemplo.com';
    this.user.photo = 'https://thenerddaily.com/wp-content/uploads/2020/02/Cosmic-Horror-Books-Lovecraft.jpg?x45557';
    this.user.password = 'contraseña';
  }

  onSubmit() {

  }
}
