import { Component } from '@angular/core';
import { User } from 'src/app/models/user';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  Profile: User;
  NewName: string;
  NewSurname: string;
  NewEmail: string;
  NewPhoto: string;

  constructor() {
    this.Profile = new User(1, "Federico", "García Lorca", "titolorca@gmail.com", "https://m.media-amazon.com/images/I/41m-5zvLhrL.png", "password");
  }

  mostrarNombre() {
    this.Profile.Name = this.NewName;
    this.Profile.Surname = this.NewSurname;
    this.Profile.email = this.NewEmail;
    this.Profile.photo = this.NewPhoto;
    console.log(this.Profile);
  }
}

<section class="profile-info">
  <h2>Datos de perfil del usuario</h2>
  <div class="profile-details">

    <img src="https://thenerddaily.com/wp-content/uploads/2020/02/Cosmic-Horror-Books-Lovecraft.jpg?x45557" alt="Imagen de perfil">
    <h3>Nombre y Apellidos malditos</h3>
    <p>Nombre profano: </p>
    <p>Apellido ancestral: </p>
    <p>Email blasfemo: </p>
  </div>
</section>

<section class="profile-form">
  <h2>Modificar datos de perfil</h2>
  <form>

    <div class="form-group">
      <label for="nombre">Nombre:</label>
      <input type="text" id="nombre" name="nombre" placeholder="Ingrese su nombre">
    </div>
    <div class="form-group">
      <label for="apellidos">Apellidos:</label>
      <input type="text" id="apellidos" name="apellidos" placeholder="Ingrese sus apellidos">
    </div>
    <div class="form-group">
      <label for="correo">Correo:</label>
      <input type="email" id="correo" name="correo" placeholder="Ingrese su correo electrónico">
    </div>
      <button type="submit">Modificar</button>
  </form>
</section>
