import { Component } from '@angular/core';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {

}
export class PerfilUsuarioComponent {
  nombre: string;
  apellidos: string;
  correo: string;
  profileImageUrl: string;

  constructor() {
    this.nombre = "Nombre del usuario";
    this.apellidos = "Apellidos del usuario";
    this.correo = "correo@ejemplo.com";
    this.profileImageUrl = "ruta_imagen_perfil";
  }
}
<section class="profile-info">
  <h2>Datos de perfil del usuario</h2>
  <div class="profile-details">
    <!-- Aquí muestra los datos de perfil: imagen, nombre, apellidos y correo -->
    <img src="ruta_imagen_perfil" alt="Imagen de perfil">
    <h3>Nombre y Apellidos</h3>
    <p>Nombre: </p>
    <p>Apellidos: </p>
    <p>Correo: </p>
  </div>
</section>

<section class="profile-form">
  <h2>Modificar datos de perfil</h2>
  <form>
    <!-- Aquí coloca los inputs para modificar los datos: nombre, apellidos, correo, URL -->
    <div class="form-group">
      <label for="nombre">Nombre:</label>
      <input type="text" id="nombre" name="nombre" placeholder="Ingrese su nombre">
    </div>
    <div class="form-group">
      <label for="apellidos">Apellidos:</label>
      <input type="text" id="apellidos" name="apellidos" placeholder="Ingrese sus apellidos">
    </div>
    <div class="form-group">
      <label for="correo">Correo:</label>
      <input type="email" id="correo" name="correo" placeholder="Ingrese su correo electrónico">
    </div>
    <div class="form-group">
      <label for="url">URL:</label>
      <input type="url" id="url" name="url" placeholder="Ingrese su URL">
    </div>
    <button type="submit">Modificar</button>
  </form>
</section>
